#include "Page.h"#include <string>using namespace std;Page::Page(){}Page::Page(string url):pageURL(url){}Page::Page(string url, string description):pageURL(url),pageDescription(description){}Page::~Page(){}	const string Page::getURL() const{	return pageURL;}void Page::setURL(const string& url){	pageURL = url;}const string Page::getDescription() const{	return pageDescription;}void Page::setDescription(const string& description){	pageDescription = description;}int Page::compare(const Page& other) const{	return pageURL.compare(other.getURL());}void Page::WriteXML(XML* xml){	xml->openTag("page");			xml->openTag("url");			xml->writeText(pageURL);		xml->closeTag();				xml->openTag("description");			xml->writeText(pageDescription);		xml->closeTag();	xml->closeTag();}